import React, { Component } from 'react';
import AddTodo from './AddTodo';
import TodoList from './TodoList';
import Footer from './Footer';

const todos = [
  {
    id: 1,
    text: '学习React',
    completed: true
  },
  {
    id: 2,
    text: '学习Redux',
    completed: false
  },
  {
    id: 3,
    text: '学习React-router',
    completed: false
  }
];

const filter = 'all';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [],
      filter: 'all'
    };
  }

  render() {
    const { todos } = this.props;
    return (
      <div>
        <AddTodo />
        <TodoList todos={todos} />
        <Footer filter={filter} />
      </div>
    );
  }
}

export default App;
